package com.anmol.service;

import java.util.HashMap;
import java.util.Map;


/**
 * Given two strings s1 and s2, write a function to return true if s2 contains the permutation of s1. In other words, one of the first string's permutations is the substring of the second string.
 *
 *
 *
 * Example 1:
 *
 * Input: s1 = "ab" s2 = "eidbaooo"
 * Output: True
 * Explanation: s2 contains one permutation of s1 ("ba").
 * Example 2:
 *
 * Input:s1= "ab" s2 = "eidboaoo"
 * Output: False
 */
public class PermutationsInString {
    Map<Character, Integer> count = new HashMap<>();
    public boolean checkInclusion(String s1, String s2) {
        for(int i = 0;i<s1.length();i++) {
            count.compute(s1.charAt(i),  (k,v) -> {
                if(v == null) {
                    return 1;
                } else {
                    return v+1;
                }
            });
        }

        for(int i = 0; i<=s2.length()-s1.length();i++) {
            if(isPermutation(s2.substring(i, i+s1.length()))) {
                return true;
            }
        }
        return false;
    }

    private boolean isPermutation(String s1) {
        Map<Character, Integer> temp = new HashMap<>();
        for(int i =0; i< s1.length();i++) {
            temp.compute(s1.charAt(i),  (k,v) -> {
                if(v == null) {
                    return 1;
                } else {
                    return v+1;
                }
            });
        }
        for(Map.Entry<Character, Integer> entry : count.entrySet()) {
            if(!entry.getValue().equals(temp.get(entry.getKey()))) {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        String s1= "";
//        String s1= "adc";
        String s2 = "";
//        String s2 = "dcda";
        PermutationsInString permutations = new PermutationsInString();
        System.out.println(permutations.checkInclusion(s1, s2));
    }

}
